    ); 


    const metodoNombre = (event) =>{
        console.log(event.targe.nombre);

    }
    const metodoApellido = (event) =>{
        console.log(event.targe.apellido);

    }

    return (
        <Fragment>
                <h1> Formulario</h1>
                <form className="row">
                    <div className="col-md-3">
                        <input 
                        placeholder="Ingrese Nombre"
                        type="text"
                        name="nombre"
                        className="form-control"
                        onChange={this.metodoNombre}></input>
                    </div>
                    <div className="col-md-3">
                        <input 
                        placeholder="Ingrese Apellido"
                        name="apellido"
                        type="text"
                        className="form-control"
                        onChange={this.metodoApellido}></input>
                    </div>
                    <div className="col-md-3">
                        <button 
                        className="btn btn-primary"
                        type="submit">Enviar</button>
                    </div>
                </form>
        </Fragment>

    );





} 
export default Formulario;
